
function getImage(url) {
    return new Promise(function(resolve, reject) {
        let img = new Image()
        img.onerror = reject
        img.onload = function() {
            resolve(img)
        }
        img.src = url
    })
}

function canvasToBlob(canvas, mimetype, quality) {
    return new Promise(function(resolve, reject) {
        canvas.toBlob(function(blob) {
            resolve(blob)
        }, mimetype, quality)
    })
}

export {getImage, canvasToBlob}
